import { ReporterService } from '../../src/reporters/service/reporter.service';
import Mocha from 'mocha';
import { XrayReporterOptions } from '../../src/reporters/model/xray-reporter-options';
import { XrayTestModel } from '../../src/reporters/model/xray-test.model';
import { JiraStatus } from '../../src/reporters/model/jira-status.enum';
import { XrayStepModel } from '../../src/reporters/model/xray-step.model';
import { XrayEvidenceModel } from '../../src/reporters/model/xray-evidence.model';

describe(`ReporterService`, () => {
  const options = {
    reporterOptions: {
      jiraProjectId: 'M2X',
      ticketPrefix: '@TICKET-',
      stepPrefix: '@STEP-',
      reportPath: 'xray.json',
    } as XrayReporterOptions,
  };

  const service = ReporterService.getInstance(options);

  it(`should be defined`, () => {
    expect(service).toBeDefined();
  });


  // TODO : Refacto of these test which doesn't test much
  describe(`buildTicketSteps`, () => {
    it(`should return XrayTestModel`, () => {
      // GIVEN
      const ticketMap = new Map<number, Mocha.Test>();
      const test = new Mocha.Test('Should end in RENONCEMENT status JIRA @TICKET-1 @STEP-1');
      jest.spyOn(test, 'titlePath').mockReturnValue(['Should end in RENONCEMENT status JIRA @TICKET-1 @STEP-1']);
      ticketMap.set(1, test);

      const screenShots = [
        'screenshots/Test screenshot @TICKET-1 @STEP-1.png',
      ];
      const options = {
        reporterOptions: {
          jiraProjectId: 'M2X',
          ticketPrefix: '@TICKET-',
          stepPrefix: '@STEP-',
          reportPath: 'xray.json',
        } as XrayReporterOptions,
      };

      // WHEN
      const xrayTestModel = service.buildTicketSteps(ticketMap, 1, screenShots, options);

      // THEN
      const expectedXrayTestModel = new XrayTestModel({
        testKey: `${options.reporterOptions.jiraProjectId}-1`,
        status: JiraStatus.PASS,
        steps: [
          new XrayStepModel({
            status: JiraStatus.PASS,
            actualResult:
              "Automated test step 1\r\nShould end in RENONCEMENT status JIRA @TICKET-1 @STEP-1\r\nDuration:undefined\r\n",
            evidences: [
              new XrayEvidenceModel({
                contentType: 'image/png',
                data: '',
                filename:
                  'Test screenshot @TICKET-1 @STEP-1.png',
              }),
            ],
          }),
        ],
      });

      expect(xrayTestModel.status).toEqual(expectedXrayTestModel.status);
      expect(xrayTestModel.steps[0].status).toEqual(expectedXrayTestModel.steps[0].status);
      expect(xrayTestModel.testKey).toEqual(expectedXrayTestModel.testKey);
    });
  });

  describe('reportFromTickets', () => {
    it('should return report', () => {
      // GIVEN
      const test = new Mocha.Test('Should end in RENONCEMENT status JIRA @TICKET-1 @STEP-1');
      jest.spyOn(test, 'titlePath').mockReturnValue(['Should end in RENONCEMENT status JIRA @TICKET-1 @STEP-1']);

      const ticketMap = new Map<number, Mocha.Test>();
      ticketMap.set(1, test);

      const tickets = new Map<number, Map<number, Mocha.Test>>();
      tickets.set(1, ticketMap);

      const options = {
        reporterOptions: {
          jiraProjectId: 'M2X',
          ticketPrefix: '@TICKET-',
          stepPrefix: '@STEP-',
          reportPath: 'xray.json',
        } as XrayReporterOptions,
      };

      // WHEN
      const xrayTestModels = service.reportFromTickets(tickets, options);

      // THEN
      const expectedXrayTestModels = [
        new XrayTestModel({
          testKey: `${options.reporterOptions.jiraProjectId}-1`,
          status: JiraStatus.PASS,
          steps: [
            new XrayStepModel({
              status: JiraStatus.PASS,
              actualResult:
                "Automated test step 1\r\nShould end in RENONCEMENT status JIRA @TICKET-1 @STEP-1\r\nDuration:undefined\r\n",
              evidences: [
                new XrayEvidenceModel({
                  contentType: 'image/png',
                  data: '',
                  filename:
                    'Test screenshot @TICKET-1 @STEP-1.png',
                }),
              ],
            }),
          ],
        }),
      ];

      expect(xrayTestModels.length).toEqual(1);
      expect(xrayTestModels[0].status).toEqual(expectedXrayTestModels[0].status);
      expect(xrayTestModels[0].testKey).toEqual(expectedXrayTestModels[0].testKey);
    });
  });

  describe('addStepToReport', () => {
    it('should add test to tickets', () => {
      const test = new Mocha.Test('Should end in RENONCEMENT status JIRA @TICKET-1 @STEP-1');
      jest.spyOn(test, 'titlePath').mockReturnValue(['Should end in RENONCEMENT status JIRA @TICKET-1 @STEP-1']);

      const options = {
        reporterOptions: {
          jiraProjectId: 'M2X',
          ticketPrefix: '@TICKET-',
          stepPrefix: '@STEP-',
          reportPath: 'xray.json',
        } as XrayReporterOptions,
      };

      const ticketMap = new Map<number, Mocha.Test>();
      ticketMap.set(1, test);

      const tickets = new Map<number, Map<number, Mocha.Test>>();

      service.addStepToReport(test, tickets, options);

      expect(tickets.get(1)).toEqual(ticketMap);
    });
  });
});
